LoadLevel_EmptyBoxesLong15:
	LDA #TILEA_BLOCKEMPTY
	
LLM15_SeTTile:
	STA <Temp_Var5

	LDA LL_ShapeDef	
	AND #$0f	
	TAX		 ; Temp_Var4 = lower 4 bits of LL_ShapeDef
	LDY TileAddr_Off	 ; Y = TileAddr_Off

PRG015_A6DD:
	LDA <Temp_Var5
	STA [Map_Tile_AddrL],Y	 ; Store into tile mem
	JSR LoadLevel_NextColumn ; Next column
	DEX		 	 ; X--
	BPL PRG015_A6DD	 	 ; While X >= 0, loop!

	RTS		 ; Return
	

	; Broken into another file for ease of integration in NoDice editor
	.include "PRG/levels/Plains.asm"
